import React from 'react';
import './styles/App.css';
import { Header, Banner, BookSection, Footer } from './components';

const App: React.FC = () => {
  // Dữ liệu mẫu cho sách
  const booksVanHoc = [
    {
      id: 1,
      title: 'Ngõ Ngô Đồng',
      price: '259,000',
      image: 'https://via.placeholder.com/220x300/667eea/ffffff?text=Ngõ+Ngô+Đồng',
      discount: '-15%'
    },
    {
      id: 2,
      title: 'Những Thế Giới Con',
      price: '175,000',
      image: 'https://via.placeholder.com/220x300/764ba2/ffffff?text=Những+Thế+Giới+Con'
    },
    {
      id: 3,
      title: 'Kim Vân Kiều',
      price: '238,000',
      image: 'https://via.placeholder.com/220x300/f093fb/ffffff?text=Kim+Vân+Kiều'
    },
    {
      id: 4,
      title: 'Tiếng Núi - Bìa Cứng',
      price: '250,000',
      image: 'https://via.placeholder.com/220x300/4facfe/ffffff?text=Tiếng+Núi',
      discount: '-20%'
    },
    {
      id: 5,
      title: 'Hai Vạn Dặm Dưới Biển',
      price: '215,000',
      image: 'https://via.placeholder.com/220x300/00f2fe/ffffff?text=Hai+Vạn+Dặm'
    },
    {
      id: 6,
      title: 'Đỉnh Gió Hú',
      price: '117,000',
      image: 'https://via.placeholder.com/220x300/43e97b/ffffff?text=Đỉnh+Gió+Hú'
    }
  ];

  const booksTamLy = [
    {
      id: 7,
      title: 'Bậc Thầy Quản Lý Thời Gian',
      price: '139,000',
      image: 'https://via.placeholder.com/220x300/fa709a/ffffff?text=Quản+Lý+Thời+Gian',
      discount: '-10%'
    },
    {
      id: 8,
      title: 'Sức Mạnh Của Kỷ Luật Bản Thân',
      price: '126,000',
      image: 'https://via.placeholder.com/220x300/fee140/333333?text=Kỷ+Luật'
    },
    {
      id: 9,
      title: 'Sự Dung Dị Của Ngôn Từ',
      price: '109,000',
      image: 'https://via.placeholder.com/220x300/30cfd0/ffffff?text=Ngôn+Từ'
    },
    {
      id: 10,
      title: 'Càng Dịu Dàng, Càng Đắt Giá',
      price: '89,000',
      image: 'https://via.placeholder.com/220x300/667eea/ffffff?text=Dịu+Dàng'
    }
  ];

  const booksKinhTe = [
    {
      id: 11,
      title: 'The Little Book - Đầu Tư Phòng Thủ',
      price: '179,000',
      image: 'https://via.placeholder.com/220x300/764ba2/ffffff?text=Đầu+Tư',
      discount: '-25%'
    },
    {
      id: 12,
      title: 'Mượn Gà Đẻ Trứng',
      price: '189,000',
      image: 'https://via.placeholder.com/220x300/f093fb/ffffff?text=Mượn+Gà'
    }
  ];

  return (
    <div className="App">
      <Header />
      <Banner />
      <BookSection title="Sách Văn Học Nước Ngoài" books={booksVanHoc} />
      <BookSection title="Sách Tâm Lý - Kỹ Năng Sống" books={booksTamLy} />
      <BookSection title="Sách Kinh Tế" books={booksKinhTe} />
      <Footer />
    </div>
  );
}

export default App;
